import React, { useEffect, useState, useContext } from 'react';
import { useLocation, Link, useNavigate } from 'react-router-dom';
import { capsuleService } from '../services/api';
import AuthContext from '../context/AuthContext';
import '../styles/SuccessPage.css';

const SuccessPage = () => {
  const location = useLocation();
  const [capsule, setCapsule] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  const { currentUser } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCapsuleData = async () => {
      try {
        // 尝试从多个来源获取数据
        let capsuleData = location.state?.capsule || 
                         JSON.parse(sessionStorage.getItem('lastCreatedCapsule'));

        // 如果仍然没有数据，尝试从API获取
        if (!capsuleData) {
          const searchParams = new URLSearchParams(location.search);
          const capsuleId = searchParams.get('id');
          
          if (capsuleId && currentUser) {
            capsuleData = await capsuleService.getCapsuleById(capsuleId, currentUser.token);
          }
        }

        if (!capsuleData) {
          throw new Error('无法获取胶囊信息');
        }

        // 验证胶囊所有者是否匹配当前用户
        if (capsuleData.creatorId !== currentUser?.id) {
          navigate('/');
          return;
        }

        setCapsule(capsuleData);
        setLoading(false);
      } catch (err) {
        console.error('获取胶囊数据失败:', err);
        setError('无法加载胶囊信息');
        setLoading(false);
      }
    };

    fetchCapsuleData();
  }, [location.state, location.search, currentUser, navigate]);
  
  // 格式化日期显示
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('zh-CN', options);
  };
  
  if (loading) {
    return (
      <div className="success-page loading">
        <div className="container text-center">
          <div className="spinner"></div>
          <p>加载中...</p>
        </div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="success-page error">
        <div className="container text-center">
          <div className="error-icon">
            <i className="fas fa-exclamation-circle"></i>
          </div>
          <h2>出错了</h2>
          <p>{error}</p>
          <Link to="/" className="btn">
            返回首页
          </Link>
        </div>
      </div>
    );
  }
  
  return (
    <div className="success-page">
      <div className="container">
        <div className="success-card">
          <div className="success-icon">
            <i className="fas fa-check-circle"></i>
          </div>
          
          <h1>时间胶囊创建成功！</h1>
          
          <div className="capsule-details">
            <h3>胶囊详情</h3>
            
            <div className="detail-item">
              <span className="detail-label">发送给:</span>
              <span className="detail-value">{capsule.recipientName}</span>
            </div>
            
            <div className="detail-item">
              <span className="detail-label">主题:</span>
              <span className="detail-value">{capsule.subject}</span>
            </div>
            
            <div className="detail-item">
              <span className="detail-label">投递日期:</span>
              <span className="detail-value">{formatDate(capsule.deliveryDate)}</span>
            </div>
          </div>
          
          <div className="capsule-id">
            <p>您的胶囊ID: <strong>{capsule.id}</strong></p>
            <p className="small">请保存此ID，以便将来查询您的胶囊状态</p>
          </div>
          
          <div className="next-steps">
            <h3>下一步</h3>
            <p>
              您的时间胶囊已经安全保存。在指定的投递日期，我们将自动将您的消息发送给接收者。
              您无需做任何事情，只需等待投递日期的到来。
            </p>
          </div>
          
          <div className="action-buttons">
            <Link to="/" className="btn">
              返回首页
            </Link>
            <Link to="/create" className="btn btn-outline">
              创建另一个胶囊
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SuccessPage;
