import React, { useState, useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import { Container, Row, Col, Form, Button, Alert, Card } from 'react-bootstrap';

const Login = () => {
  const [email, setEmail] = useState('');
  const [code, setCode] = useState('');
  const [step, setStep] = useState(1); // 1: 输入邮箱, 2: 输入验证码
  const [isLoading, setIsLoading] = useState(false);
  const [localError, setLocalError] = useState('');
  const [countdown, setCountdown] = useState(0);
  
  const { sendVerificationCode, verifyCodeAndLogin, error } = useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation();
  
  // 获取重定向路径
  const from = location.state?.from?.pathname || '/';
  
  // 处理邮箱提交
  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    setLocalError('');
    
    // 验证邮箱格式
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setLocalError('请输入有效的邮箱地址');
      return;
    }
    
    setIsLoading(true);
    try {
      await sendVerificationCode(email);
      setStep(2);
      startCountdown();
    } catch (error) {
      console.error('发送验证码失败:', error);
      setLocalError(error.response?.data?.message || '发送验证码失败，请稍后再试');
    } finally {
      setIsLoading(false);
    }
  };
  
  // 处理验证码提交
  const handleCodeSubmit = async (e) => {
    e.preventDefault();
    setLocalError('');
    
    if (!code || code.length !== 6) {
      setLocalError('请输入6位验证码');
      return;
    }
    
    setIsLoading(true);
    try {
      await verifyCodeAndLogin(email, code);
      navigate(from, { replace: true });
    } catch (error) {
      console.error('验证失败:', error);
      setLocalError(error.response?.data?.message || '验证失败，请检查验证码是否正确');
    } finally {
      setIsLoading(false);
    }
  };
  
  // 开始倒计时
  const startCountdown = () => {
    setCountdown(60);
    const timer = setInterval(() => {
      setCountdown((prevCountdown) => {
        if (prevCountdown <= 1) {
          clearInterval(timer);
          return 0;
        }
        return prevCountdown - 1;
      });
    }, 1000);
  };
  
  // 重新发送验证码
  const handleResendCode = async () => {
    if (countdown > 0) return;
    
    setIsLoading(true);
    try {
      await sendVerificationCode(email);
      startCountdown();
    } catch (error) {
      console.error('重新发送验证码失败:', error);
      setLocalError(error.response?.data?.message || '重新发送验证码失败，请稍后再试');
    } finally {
      setIsLoading(false);
    }
  };
  
  return (
    <Container className="py-5">
      <Row className="justify-content-center">
        <Col md={6}>
          <Card className="shadow-sm">
            <Card.Body className="p-4">
              <h2 className="text-center mb-4">登录</h2>
              
              {(error || localError) && (
                <Alert variant="danger">{error || localError}</Alert>
              )}
              
              {step === 1 ? (
                <Form onSubmit={handleEmailSubmit}>
                  <Form.Group className="mb-3">
                    <Form.Label>邮箱地址</Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="请输入您的邮箱"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                    />
                    <Form.Text className="text-muted">
                      我们将向此邮箱发送验证码
                    </Form.Text>
                  </Form.Group>
                  
                  <Button
                    variant="primary"
                    type="submit"
                    className="w-100"
                    disabled={isLoading}
                  >
                    {isLoading ? '发送中...' : '获取验证码'}
                  </Button>
                </Form>
              ) : (
                <Form onSubmit={handleCodeSubmit}>
                  <Form.Group className="mb-3">
                    <Form.Label>验证码</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="请输入6位验证码"
                      value={code}
                      onChange={(e) => setCode(e.target.value)}
                      maxLength={6}
                      required
                    />
                    <Form.Text className="text-muted">
                      验证码已发送至 {email}
                    </Form.Text>
                  </Form.Group>
                  
                  <Button
                    variant="primary"
                    type="submit"
                    className="w-100 mb-3"
                    disabled={isLoading}
                  >
                    {isLoading ? '验证中...' : '登录'}
                  </Button>
                  
                  <div className="text-center">
                    <Button
                      variant="link"
                      onClick={handleResendCode}
                      disabled={countdown > 0 || isLoading}
                    >
                      {countdown > 0 ? `重新发送 (${countdown}s)` : '重新发送验证码'}
                    </Button>
                  </div>
                  
                  <div className="text-center mt-3">
                    <Button
                      variant="link"
                      onClick={() => setStep(1)}
                      disabled={isLoading}
                    >
                      返回修改邮箱
                    </Button>
                  </div>
                </Form>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
